import axios from 'axios'



export function getDetail(id) {
    return async function (dispatch) {
        console.log(id);
        try{
            var json = await axios.get("http://localhost:3001/api/" + id)
            return  dispatch({
                type: 'GET_DETAIL',
                payload : json.data
            })
        }catch(e){
        console.log(e);
}}}
