import React, { useEffect  } from "react";
import { useDispatch, useSelector  } from "react-redux";
import { getDetail } from "../../actions/index";
import { useParams } from "react-router-dom";
import Grid from '@mui/material/Grid'
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));


export default function Details(props){
    const dispatch =  useDispatch()
    const { id } = useParams();


useEffect(()=>{
    dispatch(getDetail(id))
},[dispatch, id])

const details = useSelector((state) => state.detail)


    return (
        <div className="content">
            <Container fixed>
            <Box
      sx={{ width: '100%' ,height: '100%',backgroundColor: 'primary.dark','&:hover': {backgroundColor: 'primary.main',opacity: [0.9, 0.8, 0.7],},}}/>
        </Container>
            {/* <Grid container direction="row" justifyContent="center" alignItems="space-around">
                <h1>{details.name}</h1>
           <h1>{details.info}</h1>
           <h1>{details.waist}</h1>
           <h1>{details.price}</h1>
           <img src={details.image} alt="Not Found" />
            </Grid> */}
        </div>
    )
}
