import React from "react";
import {useState} from "react";
import { useDispatch } from "react-redux";
import axios from "axios";

export default function SearchBar (){
const dispatch = useDispatch()
const [name , setName] = useState("")


async function getSearch (name){
    var self = this 
        await axios.get(`http://localhost:3001/api/items?search=` + name)
        .then(function (props) {
            let item = props.data;
            self.setState({
                item : item
            })
            console.log(item);
        }).catch((err) => console.log(err))
}



function hadleInputChange(e){
    e.preventDefault()
    setName(e.target.value)
}
function hadleSubmit(e) {
    e.preventDefault()
    dispatch(getSearch(name))
    setName({
        name:""
    })
}

    return(
        <div>
            <input className="{StyleHome.btnAdmin}" type = 'text' value ={name.name} placeholder = 'Buscar pais' onChange= {(e)=> hadleInputChange(e)}/>
            <button className="{StyleHome.btnAdmin}" type = 'submit' onClick = {(e)=> hadleSubmit(e)}>Buscar</button>
        </div>
    )
}
